SimiScoreWin <- function(
    data,
    sotext,
    tatext) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("SimiScoreWin requires jmvcore to be installed (restart may be required)")

    if ( ! missing(sotext)) sotext <- jmvcore::resolveQuo(jmvcore::enquo(sotext))
    if ( ! missing(tatext)) tatext <- jmvcore::resolveQuo(jmvcore::enquo(tatext))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(sotext), sotext, NULL),
            `if`( ! missing(tatext), tatext, NULL))


    options <- SimiScoreWinOptions$new(
        sotext = sotext,
        tatext = tatext)

    analysis <- SimiScoreWinClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

